import React,{useLayoutEffect,useState} from 'react'
import { KeyboardAvoidingView } from 'react-native'
import { SafeAreaView } from 'react-native'
import { Platform } from 'react-native'
import { StyleSheet, Text, View } from 'react-native'
import { Avatar } from 'react-native-elements'
import { ScrollView, TextInput, TouchableOpacity } from 'react-native-gesture-handler'
import FontAwesome from "react-native-vector-icons/FontAwesome"
import Ionicons from 'react-native-vector-icons/FontAwesome'
const ChatScreen = ({navigation,route}) => {
    const[input,setInput]=useState('')
    useLayoutEffect(()=>{
     navigation.setOptions({
         title:"Chat",
        headerBackTitleVisible:false,
        headerTitle:()=>(
            <View style={{flexDirection:"row",alignItems:"center"}}>
                <Avatar rounded source={{uri:"https://thecomeback.com/wp-content/uploads/2016/09/b28117ea-41bc-47fe-93ef-f82a1f31717b.jpg"}}/>
                <Text style={{color:"white",fontWeight:"700",fontSize:18,marginLeft:10}}>{route.params.chatName}</Text>
            </View>
        ),
        headerRight:()=>(
            <View
             style={{
                 flexDirection:"row",
                 justifyContent:"space-between",
                 width:80,
                 marginRight:20
             }}
            >
                <TouchableOpacity>
                   <FontAwesome name="video-camera" size={24} color="white"/>

                </TouchableOpacity>
                <TouchableOpacity>
                    <Ionicons name="phone" size={24} color="white"/>
                </TouchableOpacity>
            </View>
        )
     })
    },[])
    return (
      <SafeAreaView>
          <KeyboardAvoidingView
          behavior={Platform.OS==="android"?"padding":"height"}
          style={styles.container}
          keyboardVerticalOffset={90}
          >
              <>
              <ScrollView>

              </ScrollView>
              <View style={styles.footer}>
              <TextInput placeholder="Send a message" style={styles.textInput} />
              </View>
              </>

          </KeyboardAvoidingView>
      </SafeAreaView>
    )
}

export default ChatScreen

const styles = StyleSheet.create({
    container:{

    },
    footer:{

    }
})
